mapper.select:
SELECT COUNT(*)
FROM school_users su
WHERE su.level_no = 4 AND su.user_no = #{no};

mapper.update:
update
  school_users
set
  level_no = #{levelNo}
where
  user_no = #{userNo}
and
  school_no = #{schoolNo}

mapper.delete:

SET FOREIGN_KEY_CHECKS=0;

delete
from
  school_users
where
  user_no = #{userNo}
and
  school_no = #{schoolNo}

SET FOREIGN_KEY_CHECKS=1;
